import React from 'react';
import {useState} from 'react';
import moment from 'moment';
import  './Citystyles.css';
import CityDetails from './CityDetails';
import styled from 'styled-components';
import { color } from '@mui/system';

const Mcontainer = styled.div`
  display:flex;
  justify-content: center;

  background-color: ${(props)=>(props.bcg)};
   color:${(props)=>(props.bcg)};
   border-color: ${(props)=>(props.bcg)};
`;

const details = styled.p`
  font-family: Montserrat;
  font-size:1rem;
  font-weight: bold;
`;



const ShowCity = (props) => {  

    const [showDetails, setShowDetails] = useState(false);
    const getCategorizedAQI = (aqi) => {

      let className = 'unknown';
      let impact = 'Unknown';
      let bcg = 'white';
      let cg = 'black';
      let bc = 'white';

      if (aqi >= 0 && aqi <= 50) {
        impact = 'Good';
        className = 'good';
        bcg = 'green';
        cg = 'black';
        bc = 'green';
    } else if (aqi >= 51 && aqi <= 100) {
        impact = 'Moderate';
        className = 'moderate';
        bcg = 'yellow';
        cg = 'black';
        bc = 'yellow';
    } else if (aqi >= 101 && aqi <= 150) {
        impact = 'Unhealthy for Sensitive Groups';
        className = 'unhealthy-sensitive';
        bcg = 'orange';
        cg = 'white';
        bc = 'orange';
    } else if (aqi >= 151 && aqi <= 200) {
        impact = 'Unhealthy';
        className = 'unhealthy';
        bcg = 'red';
        cg = 'white';
        bc = 'red';
    } else if (aqi >= 201 && aqi <= 300) {
        impact = 'Very Unhealthy';
        className = 'very-unhealthy';
        bcg = 'violet';
        cg = 'white';
        bc = 'violet';
    } else if (aqi >= 301) {
        impact = 'Hazardous';
        className = 'hazardous';
        bcg = 'maroon';
        cg = 'white';
        bc = 'maroon';
    }

      let catagorized = {};
      catagorized['impact'] = impact;
      catagorized['className'] = className;
      catagorized['bcg'] = bcg;
      catagorized['cg'] = cg;
      catagorized['bc'] = bc;


      return catagorized;
  };

    const getAtTimeFormatted = time => {
        return moment(time).format('h:mm:ss a');
    }



    return(
    <Mcontainer
    onClick = {()=>{setShowDetails(!showDetails)}}
    setColor = {getCategorizedAQI(props.aqi)}
    >

        <details>
        <p>At { getAtTimeFormatted(props.atTime) }: { props.placeName } - { props.aqi }</p>
        <p>{ getCategorizedAQI(props.aqi).impact }</p>

      </details>

      { showDetails && <CityDetails id={ props.uid } /> }

         {/* <div className={`cityInfo ${getCategorizedAQI(props.aqi).className}`} onClick={ () => setShowDetails(!showDetails)}>
             <List className="App">
             <p>At { getAtTimeFormatted(props.atTime) }: { props.placeName } - { props.aqi }</p>
            <div><p variant="h6">{ getCategorizedAQI(props.aqi).impact }</p></div>
            { showDetails && <CityDetails id={ props.uid } /> }
            </List>
         </div> */}

    </Mcontainer>



    );
}
export default ShowCity;
